//import PropTypes from "prop-types";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled, {css} from "styled-components";


function HomeTitle() {
  useEffect(() => {
    document.title = 'Home';
  });
}


function Home() {
  const [roomName, joinRoom] = useState(""); //방이름 state
  const [userName, joinUser] = useState(""); //유저 state
  const [storedUser, storeUser] = useState(""); //유저 저장state
  const [storedRoom, storeRoom] = useState("");
  //const [client, showClient] = useState("");

  const onUserChange = (event) => joinUser(event.target.value);
  const onUserSubmit = (event) => {  
    event.preventDefault();
    if (userName === "") {
      return;
    }
    storeUser(userName); //입력창 지우기 전, 유저 이름 storedUser 저장함.
    joinUser("");
  }
//==========방 입장 form==========
  const onRoomChange = (event) => joinRoom(event.target.value);
  const onRoomSubmit = (event) => {  
    event.preventDefault();
    if (roomName === "") {
      return;
    }
    storeRoom(roomName); //room 페이지 제목을 바꾸어줌.
    //showClient(storedUser);
    //joinRoom(""); //다시 칸을 비워줌.
  }
  

  return (
    <HomeLayout>
      <HomeTitle></HomeTitle>
      <Header>
        <div></div>
        <header>OrchestLive!</header>
        <div></div>
      </Header>

      <main>
      
      <Main>
        <div></div>
        <FormLayout>
          
            <form onSubmit={onUserSubmit}>
              <input 
                onChange={onUserChange}
                value={userName}
                type="text" 
                placeholder="user name"
              />
              <div></div>
              <button>Submit user</button>  
            </form>
          

          
            <form>
              <input 
                onChange={onRoomChange}
                value={roomName}
                type="text" 
                placeholder="room name"
              />
              <div></div>
              <Link 
                  to={{
                      pathname: "/room",
                      state: {
                          storedRoom,
                          storedUser
                      }
                  }}>
                  <button onSubmit={onRoomSubmit}>                
                      Enter room
                  </button>  
              </Link>
            </form>
          
        </FormLayout>
        <div></div>
      </Main>
      <div></div>
      </main>


      </HomeLayout>
    
  );
}

const HomeLayout = styled.div`
display: grid;
gap: 5px;
height: 90vh;
weight: 100%;
background: #B0C4DE;

div{
  grid-row: 1/2;
}
header{
  grid-row: 2/3;
  border-style: outset;
  padding-top: 0.5em;
  padding-right: 1em;
  padding-bottom: 1em;
  padding-left: 1em;
}
div{
  grid-row: 3/4;
}
main{
  grid-row: 4/12;
}
`

const Header = styled.div`
display: flex;
justify-content: space-around;

width: 100%;
height: 30px;
padding: 1em 1em;
font-size: 20px;
`
const Main = styled.div`
display: flex;
justify-content: space-around;
width: 100%;
height: 80%;
font-size: 1em;
`
const FormLayout = styled.div`
display: flex;
flex-direction: column;
justify-content: space-around;
gap : 4vh;
font-size: 20px;
`


export default Home;
