//import PropTypes from "prop-types";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled, {css} from "styled-components";


function HomeTitle() {
  useEffect(() => {
    document.title = 'Home';
  });
}


function Home() {
  const [roomName, joinRoom] = useState(""); //방이름 state
  const [userName, joinUser] = useState(""); //유저 state
  const [storedUser, storeUser] = useState(""); //유저 저장state
  const [storedRoom, storeRoom] = useState("");
  //const [client, showClient] = useState("");

  const onUserChange = (event) => joinUser(event.target.value);
  const onUserSubmit = (event) => {  
    event.preventDefault();
    if (userName === "") {
      return;
    }
    storeUser(userName); //입력창 지우기 전, 유저 이름 storedUser 저장함.
    joinUser("");
  }
//==========방 입장 form==========
  const onRoomChange = (event) => joinRoom(event.target.value);
  const onRoomSubmit = (event) => {  
    event.preventDefault();
    if (roomName === "") {
      return;
    }
    storeRoom(roomName); //room 페이지 제목을 바꾸어줌.
    //showClient(storedUser);
    //joinRoom(""); //다시 칸을 비워줌.
  }
  

  return (
    <HomeLayout>
      <HomeTitle></HomeTitle>
      <Header>
        <div></div>
        <header>OrchestLive!</header>
        <div></div>
      </Header>

      <main>
      
      <Main>
        <div></div>
        <FormLayout>
          <div className="hiUser">
            <form onSubmit={onUserSubmit}>
              <input 
                onChange={onUserChange}
                value={userName}
                type="text" 
                placeholder="user name"
              />
              <button>Submit user</button>  
            </form>
          </div>

          <div className="welcome">
            <form>
              <input 
                onChange={onRoomChange}
                value={roomName}
                type="text" 
                placeholder="room name"
              />
              <Link 
                  to={{
                      pathname: "/room",
                      state: {
                          storedRoom,
                          storedUser
                      }
                  }}>
                  <button onSubmit={onRoomSubmit}>                
                      Enter room
                  </button>  
              </Link>
            </form>
          </div>
        </FormLayout>
        <div></div>
      </Main>
      <div></div>
      </main>


      </HomeLayout>
    
  );
}

const HomeLayout = styled.div`
width: 100%;
height: 100%;
display: grid;
gap: 30px;

`

const Header = styled.div`
display: flex;
justify-content: space-around;

width: 100%;
height: 60px;
padding: 0.25em 1em;
font-size: 4em;
`
const Main = styled.div`
display: flex;

width: 80%;
height: 80%;
font-size: 2em;
justify-content: space-around;

`
const FormLayout = styled.div`

`


export default Home;
