//import logo from './logo.svg';
//import './App.css';
import { useEffect, useState } from "react";



function App() {
  const [roomName, joinRoom] = useState("");
  const [userName, joinUser] = useState(""); //유저 state
  const [insideRoom, showRoom] = useState("");
  const [client, showClient] = useState("");
  const [hideRoom, hideR] = useState(true); //hideRoom
  let userNameIs = "";
  const onUserChange = (event) => joinUser(event.target.value);
  const onUserSubmit = (event) => {  
    event.preventDefault();
    if (userName === "") {
      return;
    }
    userNameIs = userName;
    joinUser("");
  }
//==========방 입장 시===========
  const onRoomChange = (event) => joinRoom(event.target.value);
  const onRoomSubmit = (event) => {  
    event.preventDefault();
    if (roomName === "") {
      return;
    }
    showRoom(roomName); //room 페이지 제목을 바꾸어줌.
    showClient(userNameIs);
    joinRoom(""); //다시 칸을 비워줌.
    hideR(false)
  }


  return (
    <div>
      <h1>OrchestLive!</h1>
      {hideRoom? (
        <div>
          <div className="hiUser">
            <form onSubmit={onUserSubmit}>
              <input 
                onChange={onUserChange}
                value={userName}
                type="text" 
                placeholder="user name"
              />
              <button>Submit user</button>  
            </form>
          </div>

          <div className="welcome">
            <form onSubmit={onRoomSubmit}>
              <input 
                onChange={onRoomChange}
                value={roomName}
                type="text" 
                placeholder="room name"
              />
              <button>Enter room</button>  
            </form>
          </div>
        </div>
      ) : (
        <div className="call">
          <h1>Room Name : {insideRoom}</h1>
          <h3>Client : {client}</h3>
          <div className="myStream">
            <video 
              className="myFace" 
              autoPlay
              playsInline
              width="400" 
              height="400"/>
            <button className="mute">Mute</button>
            <button className="camera">Turn Camera Off</button>
            <select className="cameras"/>
            <video 
              className="peerFace" 
              autoPlay
              playsInline
              width="400" 
              height="400"/>
          </div>
        </div>
      )}
    </div>

  );
}

export default App;
