import React from "react";
import Video from "../components/Video";
import VideoContainer from "../components/VideoContainer";


function Room() {
  //console.log(props);
  const myVideo = navigator.mediaDevices.getUserMedia({
    audio: true,
    video: true,
  });
  const peerVideo1 = navigator.mediaDevices.getUserMedia({
    audio: true,
    video: true,
  });
  const peerVideo2 = navigator.mediaDevices.getUserMedia({
    audio: true,
    video: true,
  });
  const peerVideos = [
    peerVideo1, peerVideo2
  ];
  return (
    <div>
      <h1>OrchestLive!</h1>
      <div className="call">
        <h1>Room Name : </h1>
        <h3>Client : </h3>
        <VideoContainer streamArray={peerVideos} myStream={myVideo}>
        <Video stream={myVideo} autoPlay></Video>
        {/*
          peerVideos.map((key) => (
            <Video key={key} stream={item.stream} />
          ))
          */}
        </VideoContainer>
        
        <button className="mute">Mute</button>
        <button className="camera">Turn Camera Off</button>
        
        <div>
          <button className="out">Remove</button>
        </div>
      </div>
    </div>
  );
  
  
}

export default Room;

