import React, {useEffect} from "react";
//import Video from "../components/Video";
//import VideoContainer from "../components/VideoContainer";
import styled, {css} from "styled-components";


function RoomTitle() {
  useEffect(() => {
    document.title = "Room";
  });
}

function Room() {


  return (
    <RoomLayout>
      <RoomTitle></RoomTitle>
      <main>
        <Container length="4">
          <video ></video>
          <video ></video>
          <video ></video>
          <video ></video>
        </Container>
      </main>

      <footer>
        <Footer>
        <div></div>
        <div>
          <Button>
        
            <button className="mute" type="submit" >
              <img src="/microphone.png" height="27px" width="27px"></img></button>
          
            <button className="camera" type="submit" height="30px" width="30px">
              <img src="/microphone.png" height="27px" width="27px"></img></button>
          
            <button className="out" type="submit" height="30px" width="30px">
              <img src="/microphone.png" height="27px" width="27px"></img></button>
          
          </Button>
        </div>
        <div></div>
        </Footer>
      </footer>
    
    </RoomLayout>
  );
  
  
}



const RoomLayout = styled.div`
  display:grid;
  background-color: #696969;
  main{
    height: 100vh;
    width: 100%;
  }
  footer{
    height: 40px;
    width: 100%;
    position: fixed;
    bottom: 20px;
  }
  
`

const Container = styled.div`
display: grid;



background-color: #696969;
margin: 0px;
bottom: 0;
${(props) => {
  if(props.length == 1)
    return css`grid-template-columns: 80vw`
  else if(props.length == 2)
    return css`grid-template-columns: 40vw 40vw`
  else if(props.length == 3)
    return css`grid-template-columns: 30vw 30vw 30vw`
  else if(props.length == 4)
    return css`grid-template-columns: 40vw 40vw`
  else if(props.length == 5)
    return css`grid-template-columns: 25vw 25vw 25vw`
}}

video{
  width="95%";
}
`

const Footer = styled.div`
display: flex;
justify-content: space-around;
align-items: center;
background-color: #708090;
height: 60px;
`
const Button = styled.div`
display: flex;
gap: 10px;

`

export default Room;

