import { useState } from "react";

import { Link } from "react-router-dom";

function Home() {
  const [roomName, joinRoom] = useState(""); //방이름 state
  const [userName, joinUser] = useState(""); //유저 state
  const [userNameIs, storeUser] = useState(""); //유저 저장state
  const [insideRoom, showRoom] = useState("");
  const [client, showClient] = useState("");

  const onUserChange = (event) => joinUser(event.target.value);
  const onUserSubmit = (event) => {  
    event.preventDefault();
    if (userName === "") {
      return;
    }
    storeUser(userName);
    joinUser("");
  }
//==========방 입장 form==========
  const onRoomChange = (event) => joinRoom(event.target.value);
  const onRoomSubmit = (event) => {  
    event.preventDefault();
    if (roomName === "") {
      return;
    }
    showRoom(roomName); //room 페이지 제목을 바꾸어줌.
    showClient(userNameIs);
    //joinRoom(""); //다시 칸을 비워줌.
  }
  

  return (
    <div>
      <h1>OrchestLive!</h1>
      <div>
        <div className="hiUser">
          <form onSubmit={onUserSubmit}>
            <input 
              onChange={onUserChange}
              value={userName}
              type="text" 
              placeholder="user name"
            />
            <button>Submit user</button>  
          </form>
        </div>
        
        <div className="welcome">
          <form>
            <input 
              onChange={onRoomChange}
              value={roomName}
              type="text" 
              placeholder="room name"
            />
            <button onSubmit={onRoomSubmit}><Link to="/room">Enter room</Link></button>  
          </form>
        </div>
      </div>
    </div>
    
  );
}

export default Home;
