//import logo from './logo.svg';
//import './App.css';
import useState from "react";

function App() {
  const [roomName, joinRoom] = useState("");
  const onChange = (event) => console.log(event);
  const onSubmit = (event) => {
    event.preventDefault();
    if (roomName === "") {
      return;
    }
    joinRoom(""); //다시 칸을 비워줌.
  }

  return (
    <div>
      <div className="welcome">
        <form onSubmit={onSubmit}>
          <input 
            onChange={onChange}
            type="text" 
            placeholder="room name"
          />
          <button>Enter room</button>  
        </form>
      </div>

      <div className="call">
        <h1>Room Name : {roomName}</h1>
        <div className="myStream">
          <video 
            className="myFace" 
            autoplay
            playsinline
            width="400" 
            height="400"/>
          <button className="mute">Mute</button>
          <button className="camera">Turn Camera Off</button>
          <select className="cameras"/>
          <video 
            className="peerFace" 
            autoplay
            playsinline
            width="400" 
            height="400"/>
        </div>
      </div>
    </div>

  );
}

export default App;
